function clock() {
  g.clear();
  let dt = new Date();
  let m = Math.floor((dt.getTime()-_uptime)/60000);
  let h = Math.floor(m / 60); m = m % 60;
  let dz = Math.floor(h / 24); h = h % 24;
  dt = dt.toString();
  let yr = dt.indexOf("2024")+5;
  g.setFont("Default");
  g.drawString("TK78G", 120 , 40);
  g.drawString(dt.substring(0,yr), 120, 88, false);
  g.drawString(`${dz}d ${h}h ${m}m`, 120, 220);
  g.setFontATC(); //"Agency",5);
  g.drawString(`  ${dt.substring(yr,yr+5)}  `, 120, 140, false);
  g2.drawImage(g, 0, 0);
}

